void MainContentComponent::updateCache() {
	if (ApplicationConfig::Instance().GetGeneration() == ApplicationConfig::Generation::CONTRA) {
		btnContra->setToggleState(true, false);
	}
	else {
		btnMsg->setToggleState(true, false);
	}
	mListManu = ApplicationConfig::Instance().GetManu();
	lbxManufacture->UpdateList();
	lbxManufacture->repaint();
	int lastManu = ApplicationConfig::Instance().GetSelectManufacture();
	if (lastManu >= 0) {
		lbxManufacture->selectRow(lastManu, true);
		mListPlugins.clear(false);
		mListPlugins.addArray(ApplicationConfig::Instance().GetPlugins(mListManu[lastManu]), 0, ApplicationConfig::Instance().GetPlugins(mListManu[lastManu]).size());
		lbxPlugin->UpdateList();
		lbxPlugin->repaint();
		lbxPlugin->setSelectedRows(ApplicationConfig::Instance().GetSelectList(),dontSendNotification);
	}
}
