void MainContentComponent::updateScan() {
	ApplicationConfig::Instance().Save();
	ApplicationConfig::Instance().ClearList();
	ApplicationConfig::Instance().ProcessFilter();
	mListManu = ApplicationConfig::Instance().GetManu();
	lbxManufacture->UpdateList();
	lbxManufacture->repaint();

	mListPlugins.clear(false);
	lbxPlugin->UpdateList();
	lbxPlugin->repaint();
	if (mListManu.size() > 0) {
		lbxManufacture->selectRow(0, true);
		mListPlugins.addArray(ApplicationConfig::Instance().GetPlugins(mListManu[0]), 0, ApplicationConfig::Instance().GetPlugins(mListManu[0]).size());
		lbxPlugin->UpdateList();
		lbxPlugin->repaint();
	}
}
void MainContentComponent::selectedRowsChanged(ListBoxComponent *source, int lastRowSelected) {
	if (source == lbxManufacture) {
		if (lastRowSelected >= 0 && lastRowSelected < mListManu.size()) {
			ApplicationConfig::Instance().SetSelectManufacture(lastRowSelected);
			mListPlugins.clear(false);
			mListPlugins.addArray(ApplicationConfig::Instance().GetPlugins(mListManu[lastRowSelected]), 0, ApplicationConfig::Instance().GetPlugins(mListManu[lastRowSelected]).size());
			lbxPlugin->UpdateList();
			lbxPlugin->repaint();
			lbxPlugin->setSelectedRows(ApplicationConfig::Instance().GetSelectList(), dontSendNotification);
		}
	}
	else if (source == lbxPlugin) {
		ApplicationConfig::Instance().SetSelectList(lbxPlugin->getSelectedRows());
	}
}
